pragma solidity >=0.6.0;

import { BadgerBridgeZeroControllerOptimism } from "../controllers/BadgerBridgeZeroControllerOptimism.sol";
import { TransparentUpgradeableProxy } from "@openzeppelin/contracts/proxy/TransparentUpgradeableProxy.sol";
import { ProxyAdmin } from "@openzeppelin/contracts/proxy/ProxyAdmin.sol";

contract BadgerBridgeZeroControllerDeployer {
  address constant governance = 0xa1324c9085deCB8C346b69e0e951399D00AF6fb6;
  event Deployment(address indexed proxy);

  constructor() {
    address logic = address(new BadgerBridgeZeroControllerOptimism());
    ProxyAdmin proxy = new ProxyAdmin();
    ProxyAdmin(proxy).transferOwnership(governance);
    emit Deployment(
      address(
        new TransparentUpgradeableProxy(
          logic,
          address(proxy),
          abi.encodeWithSelector(BadgerBridgeZeroControllerOptimism.initialize.selector, governance, governance)
        )
      )
    );
    selfdestruct(msg.sender);
  }
}
